import React, { useEffect, useState } from 'react';
import { Avatar } from '@material-ui/core';
import './SidebarChat.css';
import db from './firebase';
import { Link } from 'react-router-dom';

function SidebarChat({ addNewChat, id, name }) {
	const [seed, setSeed] = useState('');
	const [messages, setMessages] = useState('');

	useEffect(() => {
		setSeed(Math.floor(Math.random() * 5000));
	}, []);

	const createChat = () => {
		const roomName = prompt('请输入房间名称');
		if (roomName) {
			db.collection('rooms').add({
				name: roomName,
			});
		}
	};

	useEffect(() => {
		if (id) {
			db.collection('rooms')
				.doc(id)
				.collection('message')
				.orderBy('time', 'desc')
				.onSnapshot((snapshot) =>
					setMessages(snapshot.docs.map((doc) => doc.data()))
				);
			const a = messages[messages.length - 1]?.time;
			const b = messages[messages.length - 1]?.time?.toDate();
			const c = new Date(messages[messages.length - 1]?.time?.toDate());
			console.log(a, b, c);
		}
	}, [id]);

	return !addNewChat ? (
		<Link to={`/rooms/${id}`}>
			<div className='sidebarChat'>
				<Avatar src={`https://avatars.dicebear.com/api/human/${seed}.svg`} />
				<div className='sidebarChat__info'>
					<h2>{name}</h2>
					<div className='sidebarChat__message'>{messages[0]?.message}</div>
					<div className='sidebarChat__timestamp'>
						{new Date(
							messages[messages.length - 1]?.time?.toDate()
						).toUTCString()}
					</div>
				</div>
			</div>
		</Link>
	) : (
		<div onClick={createChat} className='sidebarChat'>
			<h3>add new chat</h3>
		</div>
	);
}

export default SidebarChat;
